package bookPoo;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 6;
		switch (a) {
		case 1:
			System.out.println("El valor de a es " + a);
			break;
		case 2:
			System.out.println("El valor de a es " + a);
			break;
		case 3:
			System.out.println("El valor de a es " + a);
			break;
		default:
			System.out.println("El valor de a es " + a + " es incorrecto");
			break;
		}

	}

}
